import React, { useState, useEffect } from 'react';
import { Form, Input, Button, notification } from 'antd';
import {
    KeyOutlined,
    GiftOutlined,
    DollarOutlined,
    LinkOutlined
} from '@ant-design/icons';
import { getAccessTokenApi } from '../../../../api/auth';
import { addCourseApi } from '../../../../api/courses';

import './AddEditCourseForm.scss';

export default function AddEditCourseForm(props) {
    const { setIsVisibleModal, setReloadCourse, course } = props;
    const [courseData, setCourseData] = useState({});

    const addCourse = e => {
        e.preventDefault()
        console.log('creando curso');
    }

    const updateCourse = e => {
        e.preventDefault()
        console.log('actualizando curso!!!');
    }

    return (
        <div className='add-edit-course-form'>
            <AddEditForm 
                course={course}
                addCourse={addCourse}
                updateCourse
            />
        </div>
    )
}


function AddEditForm(props) {
    const { course } = props;
    return (
        <Form className='form-add-edit' onSubmitCapture={() => console.log('submit form')}>
            <Form.Item>
                <Input 
                    prefix={<KeyOutlined />}
                    placeholder='Id del curso.'
                    // value={ }
                    // onChange={ }
                    disabled={course ? true : false}
                />
            </Form.Item>
            <Form.Item>
                <Input 
                    prefix={<GiftOutlined />}
                    placeholder='Cupon de descuento.'
                    // value={ }
                    // onChange={ }
                />
            </Form.Item>
            <Form.Item>
                <Input 
                    prefix={<LinkOutlined />}
                    placeholder='URL del curso.'
                    // value={ }
                    // onChange={ }
                />
            </Form.Item>
            <Form.Item>
                <Input 
                    prefix={<DollarOutlined />}
                    placeholder='Precio del curso.'
                    // value={ }
                    // onChange={ }
                />
            </Form.Item>
            <Form.Item>
                <Button type='primary' htmlType='submit' className='btn-submit'>
                    {course ? 'Actualizar curso' : 'Crear curso'}
                </Button>
            </Form.Item>
        </Form>
    )
}
