import React from 'react'
import { List, Modal, Button, notification } from 'antd';
import {
    EyeOutlined,
    EditOutlined,
    DeleteOutlined
} from '@ant-design/icons';
import { Link } from 'react-router-dom';
import { getAccessTokenApi } from '../../../../api/auth';
import { deletePostApi } from '../../../../api/post';

import './PostsList.scss';

const { confirm } = Modal;

export default function PostList(props) {

    const deletePost = post => {
        const accessToken = getAccessTokenApi();

        confirm({
            title: 'Eliminando Post.',
            content: `¿Estas seguro que quieres eliminar el post ${post.title}?`,
            okText: 'Eleminar',
            okType: 'danger',

        })
    }

    const { posts } = props;

    console.log(posts);

    return (
        <div className='posts-list'>
            <List 
                dataSource={posts.docs}
                renderItem={post => <Post post={post} deletePost={deletePost}/>}

            />
        </div>
    )
}

function Post(props) {
    const { post, deletePost } = props;

    return (
        <List.Item
            actions={[
                <Link to={`/blog/${post.url}`} target='_blank'>
                    <Button type='primary' ><EyeOutlined /></Button>
                </Link>,
                <Button type='primary'><EditOutlined /></Button>,
                <Button type='danger' onClick={() => deletePost(post)}><DeleteOutlined /></Button>
            ]}
        >
            <List.Item.Meta title={post.title} />
        </List.Item>
    )
}
