import React, { useState } from 'react';
import { Switch, List, Avatar, Button } from 'antd';
import {
    EditOutlined,
    StopOutlined,
    DeleteOutlined,
    CheckOutlined
} from '@ant-design/icons';
import NoAvatar from '../../../../assets/img/png/noAvatar.png';
import Modal from '../../../Modal';

import './ListUsers.scss';

export default function ListUsers(props){
    const { usersActive, usersInactive } = props;
    const [viewUsersActives, setViewUsersActives] = useState(true);
    const [isVisibleModal, setIsVisibleModal] = useState(false);
    const [modalTitle, setModalTitle] = useState('');
    const [modalContent, setModalContent] = useState(null);

    return (
        <div className='list-users'>
            <div className='list-users__switch'>
                <Switch 
                    defaultChecked
                    onChange={() => setViewUsersActives(!viewUsersActives)}
                />
                <span>{viewUsersActives ? ' Usuarios Activos' : ' Usuarios inactivos'}</span>
            </div>
            {viewUsersActives ? <UsersActive usersActive={usersActive} /> : <UsersInactive usersInactive={usersInactive} />}
            <Modal
                title={modalTitle}
                isVisible={isVisibleModal}
                setIsVisible={setIsVisibleModal}
            >{modal}</Modal>
        </div>
    )
}

function UsersActive(props) {
    const { usersActive } = props;

    return (
        <List 
            className='users-active'
            itemLayout='horizontal'
            dataSource={usersActive}
            renderItem={user => (
                <List.Item
                    actions={[
                        <Button type='primary' onClick={() => console.log('edit user')} ><EditOutlined /></Button>,
                        <Button type='danger' onClick={() => console.log('desactivar user')} ><StopOutlined /></Button>,
                        <Button type='danger' onClick={() => console.log('delete user')} ><DeleteOutlined /></Button>
                    ]}
                >
                    <List.Item.Meta 
                        avatar={<Avatar src={user.avatar ? user.avatar : NoAvatar} />}
                        title={`
                            ${user.name ? user.name : '...'} ${user.lastname ? user.lastname : '...'}
                        `}
                        description={user.email}
                    />
                </List.Item>
            )}
        />
    )
}
function UsersInactive(props) {
    const { usersInactive } = props;
    return (
        <List 
            className='users-active'
            itemLayout='horizontal'
            dataSource={usersInactive}
            renderItem={user => (
                <List.Item
                    actions={[
                        <Button type='primary' onClick={() => console.log('active user')} ><CheckOutlined /></Button>,
                        <Button type='danger' onClick={() => console.log('delete user')} ><DeleteOutlined /></Button>
                    ]}
                >
                    <List.Item.Meta 
                        avatar={<Avatar src={user.avatar ? user.avatar : NoAvatar} />}
                        title={`
                            ${user.name ? user.name : '...'} ${user.lastname ? user.lastname : '...'}
                        `}
                        description={user.email}
                    />
                </List.Item>
            )}
        />
    )
}
