import React, { useState, useEffect } from 'react';
import { Row, Form, Input, Col, Button, DatePicker, notification } from 'antd';
import {
    FontSizeOutlined,
    LinkOutlined
} from '@ant-design/icons';
import moment from 'moment';
import { Editor } from '@tinymce/tinymce-react';
import { getAccessTokenApi } from '../../../../api/auth';
import { addPostApi, updatePostApi } from '../../../../api/post';



import './AddEditPostForm.scss';

export default function AddEditPostForm(props) {

    const { setIsVisibleModal, setReloadPosts, post } = props;
    const [postData, setPostData] = useState({});

    useEffect(() => {
        if (post) {
            setPostData(post)
        } else {
            setPostData({})
        }
    }, [post])

    const processPost = e => {
        e.preventDefault()
        const { title, url, description, date } = postData;

        if (!title || !url || !description || !date) {
            notification['error']({message: 'Todos los campos son obligatorios.'})
        } else {
            if (!post) {
                addPost()
            } else {
                updatePost()
            }
        }
    }

    const addPost = () => {
        const token = getAccessTokenApi()

        addPostApi(token, postData)
            .then(response => {
                const typeNotification = response.code === 200 ? 'success' : 'warning'
                notification[typeNotification]({ message: response.message })
                setIsVisibleModal(false)
                setReloadPosts(true)
                setPostData({})
            })
            .catch(() => {
                notification['error']({message: 'Error del servidor.'})
            })
    }

    const updatePost = () => {
        const token = getAccessTokenApi()

        updatePostApi
    }

    return (
        <div className='add-edit-post-form'>
            <AddEditForm postData={postData} setPostData={setPostData} post={post} processPost={processPost} />
        </div>
    )
};

function AddEditForm(props) {
    const { postData, setPostData, post, processPost } = props

    return (
        <Form className='add-edit-post-form' layout='inline' onSubmitCapture={processPost} >
            <Row gutter={24}>
                <Col span={8}>
                    <Input 
                        prefix={<FontSizeOutlined />}
                        placeholder='Titulo'
                        value={postData.title}
                        onChange={e => {
                            setPostData({...postData, title: e.target.value})
                        }}
                    />
                </Col>
                <Col span={8}>
                    <Input 
                        prefix={<LinkOutlined />}
                        placeholder='URL'
                        value={postData.url}
                        onChange={e => {
                            setPostData({...postData, url: transformTextUrl(e.target.value)})
                        }}
                    />
                </Col>
                <Col span={8}>
                    <DatePicker 
                        style={{ width: '100%' }}
                        format="DD/MM/YYYY HH:mm:ss"
                        placeholder='Fecha de publicaciÃ³n'
                        showTime={{defaultValue: moment("00:00:00", "HH:mm:ss")}}
                        value={postData.date && moment(postData.date)}
                        onChange = {
                            (e, value) => setPostData({
                                ...postData,
                                date: moment(value, "DD/MM/YYYY HH:mm:ss").toISOString()
                            })
                        }
                    />
                </Col>
            </Row>
            <Editor
                value={postData.description ? postData.description : ''}
                init={{
                    height: 400,
                    width: '90%',
                    menubar: true,
                    plugins: [
                        'advlist autolink lists link image charmap print preview anchor',
                        'searchreplace visualblocks code fullscreen',
                        'insertdatetime media table paste code help wordcount'
                    ],
                    toolbar:
                        'undo redo | formatselect | bold italic backcolor | \
                        alignleft aligncenter alignright alignjustify | \
                        bullist numlist outdent indent | removeformat | help'
                }}
                onBlur={e => setPostData({...postData, description: e.target.getContent()})}
            />
            <Button type='primary' htmlType='submit' className='btn-submit'>
                {post ? 'Actualizar Post' : 'Crear Post'}
            </Button>
        </Form>
    )
}

function transformTextUrl(text) {
    const url = text.replace(" ", "-")
    return url.toLowerCase()
}
