import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Button, Rate, notification } from 'antd';
import { getCourseDataUdemyApi } from '../../../../api/courses';

import './CoursesList.scss';

export default function CoursesList(props) {
    const { courses } = props;

    return (
        <div className='courses-list'>
            <Row>
                {courses.map(course => (
                    <Col md={8} className='courses-list__course' key={course._id}>
                        <Course  course={course} />
                    </Col>
                ))} 
            </Row>
        </div>
    )
}

function Course(props) {
    const { course } = props;
    const { Meta } = Card;

    const [courseInfo, setCourseInfo] = useState({})

    useEffect(() => {
        getCourseDataUdemyApi(course.idCourse)
            .then(response => {
                if (response?.code !== 200) {
                    notification['warning']({message: response.message})
                } else {
                    setCourseInfo(response.data)
                }
            })
            .catch(() => {
                notification['error']({message: 'Error del servidor, intentelo más tarde.'})
            })
    }, [course])

    return (
        <a href='#' target='_blank' rel='noopener noreferrer'>
            <Card
                cover={<img src={courseInfo.image_480x270} alt={courseInfo.title} />}
            >
                <Meta title={courseInfo} />

            </Card>
        </a>
    )
}
