import React, { useCallback, useState, useEffect } from 'react';
import { Avatar, Form, Input, Select, Button, Row, Col, notification } from 'antd';
import {
    UserOutlined,
    MailOutlined,
    LockOutlined
} from '@ant-design/icons';
import { useDropzone } from 'react-dropzone';

import NoAvatar from '../../../../assets/img/png/noAvatar.png';
import { uploadAvatarApi, getAvatarApi, updateUserApi } from '../../../../api/user';
import { getAccessTokenApi } from '../../../../api/auth';

import './EditUserForm.scss';

export default function EditUserForm(props) {

    const { user } = props;
    const [avatar, setAvatar] = useState(null)
    const [userData, setUserData] = useState({})

    useEffect(() => {
        setUserData({
            name: user.name,
            lastname: user.lastname,
            email: user.email,
            role: user.role,
            avatar: user.avatar 
        })
    },[user])

    useEffect(() => {
        if (user.avatar) {
            getAvatarApi(user.avatar).then(response => {
                setAvatar(response);
            })
        } else {
            setAvatar(null)
        }
    },[user])

    useEffect(() => {
        if (avatar) {
          setUserData({ ...userData, avatar: avatar.file });  
        }
    }, [avatar])

    const updateUser = e => {
        e.preventDefault();

        const token = getAccessTokenApi();
        const userUpdate = userData;

        if (userUpdate.password || userUpdate.repeatPassword) {
            if (userUpdate.password !== userUpdate.repeatPassword) {
                notification['error']({message: 'Las contrase単as tienen que ser iguales.'})
            }
            return
        }

        if (!userUpdate.name || !userUpdate.lastname || !userUpdate.email) {
            notification['error']({ message: 'El nombre, los apellidos y el email son obligatorios.' })
            return;
        }

        if (typeof userUpdate.avatar === 'object') {
            uploadAvatarApi(token, user)
        }
    }

    return (
        <div className="edit-user-form">
            <UploadAvatar avatar={avatar} setAvatar={setAvatar} />
            <EditForm userData={userData} setUserData={setUserData} updateUser={updateUser} />
        </div>
    )
}

function UploadAvatar(props) {
    const { avatar, setAvatar } = props;
    const [avatarUrl, setAvatarUrl] = useState(null);

    useEffect(() => {
        if (avatar) {
            if (avatar.preview) {
                setAvatarUrl(avatar.preview)
            } else {
                setAvatarUrl(avatar)
            }
        } else {
            setAvatarUrl(null)
        }
    },[avatar])

    const onDrop = useCallback(
    acceptedFiles => {
      const file = acceptedFiles[0];
      setAvatar({ file, preview: URL.createObjectURL(file) });
    },
    [setAvatar]
  );

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: "image/jpeg, image/png",
    noKeyboard: true,
    onDrop
  });

    return (
        <div className='upload-avatar' {...getRootProps()}>
            <input {...getInputProps()} />
            {isDragActive ? (
                <Avatar size={150} src={NoAvatar}/>
            ) : (
                    <Avatar size={150} src={avatarUrl ? avatarUrl : NoAvatar} />
            )}
        </div>
    )
}

function EditForm(props) {
    const { userData, setUserData, updateUser } = props;

    const { Option } = Select;

    return (
        <Form className="form-edit" onSubmitCapture={updateUser}>
            <Row gutter={24}>
                <Col span={12}>
                    <Form.Item>
                        <Input 
                            prefix={<UserOutlined />}
                            placeholder="Nombre"
                            value={userData.name}
                            onChange={e => setUserData({ ...userData, name: e.target.value})}
                        />
                    </Form.Item>
                </Col>
                <Col span={12}>
                    <Form.Item>
                        <Input 
                            prefix={<UserOutlined />}
                            placeholder="Apellido"
                            value={userData.lastname}
                            onChange={e => setUserData({ ...userData, lastname: e.target.value})}
                        /> 
                    </Form.Item>
                </Col>
            </Row>

            <Row gutter={24}>
                <Col span={12}>
                    <Form.Item>
                        <Input 
                            prefix={<MailOutlined />}
                            placeholder="Email"
                            value={userData.email}
                            onChange={e => setUserData({ ...userData, email: e.target.value})}
                        />
                    </Form.Item>
                </Col>
                <Col span={12}>
                    <Form.Item>
                        <Select
                        placeholder="selecciona un Rol"
                        onChange={e => setUserData({ ...userData, role: e })}
                        value={userData.role}
                        >
                        <Option value="admin">Administrador</Option>
                        <Option value="editor">Editor</Option>
                        <Option value="reviewr">Revisor</Option>
                        </Select>
                    </Form.Item>
                </Col>
            </Row>

            <Row gutter={24}>
                <Col span={12}>
                    <Form.Item>
                        <Input 
                            prefix={<LockOutlined />}
                            type="password"
                            placeholder="Contrase単a"
                            onChange={e => setUserData({ ...userData, password: e.target.value})}
                        />
                    </Form.Item>
                </Col>
                <Col span={12}>
                    <Form.Item>
                        <Input 
                            prefix={<LockOutlined />}
                            type="password"
                            placeholder="Repetir contrase単a"
                            onChange={e => setUserData({ ...userData, repeatPassword: e.target.value})}
                        />
                    </Form.Item>
                </Col>
            </Row>

            <Form.Item>
                <Button type="primary" htmlType="submit" className="btn-submit" >
                    Actualizar Usuario
                </Button>
            </Form.Item>
        </Form>
    )
}
